import React from 'react';
import ReactDOM from 'react-dom';
import {ReactComponent as SVGFloor_1} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor0} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor1} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor2} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor3} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor4} from './SVGs/elevator_button.svg'

const Hello = (
	<p>Hellow World!</p>
	);

const Floor1 = () => {
	return <SVGFloor1 />;
}

class Point {
	constructor(x, y) {
		this.x = x;
		this.y = y;
	}
}

class Floor extends React.Component {
	constructor(svgplan, n, points) {
		super();
		this.plan = svgplan;
		this.position = n;
		this.points = points;
		this.state = {
			isCurrent : 0
		};
	}
	render() {
		return;
	}
}

//Floor1 = new Floor(<SVGFloor1/>, 1, [])

class FloorPlan extends React.Component {
	constructor() {
		super();
		this.floors = [
			new Floor (<SVGFloor_1/>, -1, []),
			new Floor (<SVGFloor0/>, 0, []),
			new Floor (<SVGFloor1/>, 1, []),
			new Floor (<SVGFloor2/>, 2, []),
			new Floor (<SVGFloor3/>, 3, []),
			new Floor (<SVGFloor4/>, 4, [])
		];
		this.state = {
			currentFloor: 1
		};
	}
	changeFloor = (n) => {
		this.setState({currentFloor: n});
		this.floors[n+1].setState({isCurrent: 1});
	}
	render() {
		for (floor in this.floors) {
			<floor/>;
		}
	}
}

ReactDOM.render(Hello,document.getElementById('root'));
