import React from 'react';
import ReactDOM from 'react-dom';
import {ReactComponent as SVGFloor_1} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor0} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor1} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor2} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor3} from './SVGs/elevator_button.svg'
import {ReactComponent as SVGFloor4} from './SVGs/elevator_button.svg'

const Hello = (
	<p>Hellow World!</p>
	);


const Floor_1 = () => {
	return (
		<SVGFloor_1 />
	);
}
const Floor0 = () => {
	return <SVGFloor0 />;
}
const Floor1 = () => {
	return <SVGFloor1 />;
}
const Floor2 = () => {
	return <SVGFloor2 />;
}
const Floor3 = () => {
	return <SVGFloor3 />;
}
const Floor4 = () => {
	return <SVGFloor4 />;
}

class Point {
	constructor(x, y) {
		this.x = x;
		this.y = y;
	}
}

class Floor extends React.Component {
	constructor(svgplan, n, points) {
		super();
		this.plan = svgplan;
		this.position = n;
		this.points = points;
		this.state = {
			isCurrent : 0
		};
	}
	render() {
		return this.plan ;
	}
}

//Floor1 = new Floor(<SVGFloor1/>, 1, [])

class FloorPlan extends React.Component {
	constructor() {
		super();
		this.floors = [
			new Floor (Floor_1, -1, []),
			new Floor (Floor0, 0, []),
			new Floor (Floor1, 1, []),
			new Floor (Floor2, 2, []),
			new Floor (Floor3, 3, []),
			new Floor (Floor4, 4, [])
		];
		this.state = {
			currentFloor: 1
		};
	}
	changeFloor = (n) => {
		this.setState({currentFloor: n});
		this.floors[n+1].setState({isCurrent: 1});
	}
	render() {
		return (
			this.floors[0].plan
		);
	}
}

ReactDOM.render(<SVGFloor_1 />,document.getElementById('root'));
