import React from 'react';
import ReactDOM from 'react-dom';
import UI from './Icons/uiv2.js';
import './App.css';

import paths from './paths.js';
import events from './events.js'

const TimeBetweenFloors = 500; //ms

class Chemin extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
			dotPosition: this.props.dotPosition || this.props.points[0],
			dotX: "4",
			dotY: "4",
			thickness: 3,
			color: "#2400FF"
		};
	}
	render() {
		var path_d = "M" + this.props.points[0].x.toString() + " " + this.props.points[0].y.toString();
		for (const point of this.props.points.slice(1)) {
			path_d = path_d + "L" + point.x.toString() + " " + point.y.toString();
		}
		console.log(path_d);
		return (
			<svg width="300" height="300">
				<path d={path_d} strokeWidth={this.state.thickness} stroke={this.state.color} fill={"none"}/>
				<ellipse id="EndPoint" cx={this.state.dotPosition.x} cy={this.state.dotPosition.y} rx={this.state.dotX} ry={this.state.dotY} fill={this.state.color}/>
			</svg>
		);
	}
}

class Button extends React.Component {
	render() {
		return (
			<button onClick={this.props.floorUp}>
				{this.props.text}
			</button>
		);
	}
}

class FloorPlan extends React.Component {
	constructor() {
		super();
		this.state = {
			selectedEvent: 0
		};
	}

	render() {
		selectedEvent = this.state.selectedEvent
		steps = paths[events[selectedEvent].salle].steps;
		pathFromAfficheur = paths[events[selectedEvent].salle].etage !== 1 ? paths["ascenceur"] : paths[events[selectedEvent].salle].trace;
		pathToSalle = paths[events[selectedEvent].salle].trace;
		return (
			<div className="viewbox">
			{/*<div>
				<Button floorUp = {this.floorUp} text = {"floorUp"}/>
				50<Button floorUp = {this.floorDown} text = {"floorDown"}/>
			</div>*/}
			<div style={{textAlign: "center"}}>
				{/*<UI />*/}
			</div>
			<div className="etageAfficheur plan" id={paths[events[selectedEvent].salle].etage === 1 ? "noChange" : "top"}>
				<div>
					<Chemin points={pathFromAfficheur} dotPosition={pathFromAfficheur[0]} />
				</div>
			</div>

			<div className={paths[events[selectedEvent].salle].etage !== 1 ? "plan bottom" : "hidden"} id={"floor"+paths[events[selectedEvent].salle].etage.toString()}>
				<div>
					<Chemin points={pathToSalle} dotPosition={pathToSalle[0]} />
				</div>
			</div>

			<div className={steps[0] !== "" ? "step" : "hidden"} id="step1">
				<p>{steps[0]}</p>
			</div>
			<div className={steps[1] !== "" ? "step" : "hidden"} id="step2">
				<p>{steps[1]}</p>
			</div>
			<div className={steps[2] !== "" ? "step" : "hidden"} id="step3">
				<p>{steps[2]}</p>
			</div>
			<div className={steps[3] !== "" ? "step" : "hidden"} id="step4">
				<p>{steps[3]}</p>
			</div>

			<div className={events[0].intitule !== "" ? "event" : "hidden"} id="event1">
				<div className="horaire">
					<p>{events[0].horaire}</p>
				</div>
				<div className="intitule">
					<p>{events[0].intitule}</p>
				</div>
				<div className="salle">
					<p>{events[0].salle}</p>
				</div>
			</div>

			<div className={events[1].intitule !== "" ? "event" : "hidden"} id="event2">
				<div className="horaire">
					<p>{events[1].horaire}</p>
				</div>
				<div className="intitule">
					<p>{events[1].intitule}</p>
				</div>
				<div className="salle">
					<p>{events[1].salle}</p>
				</div>
			</div>

			<div className={events[2].intitule !== "" ? "event" : "hidden"} id="event3">
				<div className="horaire">
					<p>{events[2].horaire}</p>
				</div>
				<div className="intitule">
					<p>{events[2].intitule}</p>
				</div>
				<div className="salle">
					<p>{events[2].salle}</p>
				</div>
			</div>

			</div>
		);
	}
}

ReactDOM.render(<FloorPlan events={events} room="Salle de conférence"/>,document.getElementById('root'));
